import React from 'react';
import styles from './CreateAccount.module.css';
import Button from '../Components/Button';
import Input from '../Components/Input';
import { POST_USER } from '../api';

const CreateAccount = () => {
  const [nome, setNome] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [senha, setSenha] = React.useState('');

  function createAccount(nome, email, senha) {
    const { url, options } = POST_USER({
      nome: nome,
      email: email,
      senha: senha,
    });
    fetch(url, options)
      .then((response) => response.json())
      .then((json) => console.log(json));
  }
  return (
    <div className={`animeLeft ${styles.modal}`}>
      <div className={styles.loginLeft}></div>
      <div className={styles.loginRight}>
        <div className={styles.container}>
          {' '}
          <h1>Sign Up</h1>
          <Input
            type="text"
            id="User"
            placeholder="User"
            onChange={({ target }) => setNome(target.value)}
          />
          <Input
            type="email"
            id="login"
            placeholder="Email"
            onChange={({ target }) => setEmail(target.value)}
          />
          <Input
            type="password"
            id="password"
            placeholder="Password"
            onChange={({ target }) => setSenha(target.value)}
          />
          <Button text="Sign Up" onClick={createAccount} />
          <p>Do you have Account? Sign In</p>
        </div>
      </div>
    </div>
  );
};

export default CreateAccount;
