import React from 'react';
import styles from './Home.module.css';
import { GET_FILMS } from '../api';
import Button from '../Components/Button';
import ButtonIcon from '../Components/ButtonIcon';
import Input from '../Components/Input';

export const Home = () => {
  const [dados, setDados] = React.useState(null);
  React.useEffect(() => {
    const { url, options } = GET_FILMS();
    fetch(url, options)
      .then((response) => response.json())
      .then((json) => setDados(json));
  }, [dados]);

  const mystyle = {
    marginLeft: '0px',
    width: '30%',
  };
  if (dados == null) return null;
  return (
    <>
      <div className={styles.container}>
        <div className={styles.supContainer}></div>
        <div className={styles.containerNav}>
          <ButtonIcon text="Add Films" />
          <Input style={mystyle} />
        </div>
        {/* <h1 className={styles.title}>LocalFilms 3000</h1> */}
        <div className={styles.films}>
          {dados.map((filme) => (
            <div key={filme.id} className={styles.containerPhoto}>
              <img src={filme.foto} alt={filme.foto} />
              <section>
                <h1>{filme.nome}</h1>
                <p>Genero: {filme.genero}</p>
                <p>Duração: {filme.duracao} minutos</p>
              </section>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};
